import React, { useState} from 'react';

const ActualGame = () => {

    const [selectedImage, setSelectedImage] = useState(null);
  const [classification, setClassification] = useState('');

  const showImage = (imageSrc) => {
    setSelectedImage(imageSrc);
  };

  const handleClassificationChange = (event) => {
    setClassification(event.target.value);
  };

    return (
      <div>
        <div className="header">
            <img className="header-img" src="/assets/images/logo-white.png" alt = "Phooled header logo"></img>
        </div>
        <div className="container-rules">
            <div className="stat">
            <img  className="heart" src='assets/images/heart.png'></img>
            </div>
        </div>
        <div className="image-container" onClick={() => showImage('/assets/images/sample1.png')}>
          <img src="image1.jpg" alt="Image 1" />
        </div>
        <div className="image-container" onClick={() => showImage('/assets/images/sample2.png')}>
          <img src="image2.jpg" alt="Image 2" />
        </div>
        <div className="image-container" onClick={() => showImage('/assets/images/sample3.png')}>
          <img src="image3.jpg" alt="Image 3" />
        </div>

        <div>
          {selectedImage && (
          <div>
            <h3>Selected Image</h3>
            <img src={selectedImage} alt="Selected" />
            <div>
              <label htmlFor="classification">Classification:</label>
              <select id="classification" value={classification} onChange={handleClassificationChange}>
                <option value="">Select Classification</option>
                <option value="phishing">Phishing</option>
                <option value="non-phishing">Non-Phishing</option>
              </select>
            </div>
          </div>
        )}
      </div>
    </div>
    );
}

export default ActualGame;
