import React from 'react';

const Game = () => {
  return (
    <div class="hero w-full h-full bg-base-100 shadow-lg rounded-lg p-4">
      <div class="hero-content text-center">
        <div class="max-w-md">
          <h1 class="text-5xl font-bold">Game Lobby</h1>
          <p x-show="isOwner === 1" class="py-6">Please start the game when all players have joined.</p>
          <p x-show="isOwner === 0" class="py-6">Waiting for the Host to start the game.</p>
          <button x-show="isOwner === 1" class="btn btn-primary" onclick="sendStartMessage()">
            Start
          </button>
        </div>        
      </div>      
    </div>
  );

};

export default Game;
