import React, {Component } from 'react';
import './Play.css';

class Play extends Component {
    render() {
      return (
        <div>
          <div class="header">
            <img class="header-img" src='assets/images/logo-white.png'></img>
          </div>
          <div class="">
            <div class="hero-content text-center">
              <div class="max-w-md">
                <h1 class="text-5xl font-bold">Game Lobby</h1>
                <p x-show="isOwner === 1" class="py-6">
                  Please start the game when all players have joined.
                </p>

                <button
                  x-show="isOwner === 1"
                  class="btn btn-primary"
                  >
                  Start
                </button>
              </div>
            </div>
        </div>
      </div>
      );
    }
  }
export default Play;
