// src/components/Login.js
import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import './cirrus-core.min.css';
import './cirrus.min.css';
import './layout.css';
import './phishducation.css';

const Login = ({history}) => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async () => {
        try {
            const response = await fetch('http://localhost:8000/api/login/', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({ username, password }),
            });
            console.log(response)
            if (response.ok) {
              // Signup successful, you might want to redirect to login or do other actions
              console.log('Login successful');

            } else {
              // Signup failed, check the response for specific error message
              const data = await response.json();
              console.log("i am data", data)
              console.log("i am data", data.detail)
              setError(data.detail); // Assuming the server sends a message field in the response
              if (data.detail === 'Invalid credentials') {
                // User already exists, navigate to the login page
                navigate('/signup');
              }
            }
          } catch (error) {
            console.error('Error during signup:', error);
          }
    };

    return (
        <div>
          <Header />
            <div class="container">
	            <div class="row">
		            <div class="col-sm-12 col-lg-4 my-auto" data-aos="fade-right" data-aos-duration="1000" data-aos-once="true" data-aos-easing="ease-in-sine">
    		          <div class="main-login-content" style={{backgroundColor: "#C3D8E6", padding: "30px", borderRadius: "25px"}}>
        	          <h4 class="login-header"><i class="fas fa-sign-in-alt" style={{paddingRight: "5px"}}></i>Login </h4>
        	          <form method="post" action="" style={{width: "100%"}}>
				              <div>
					              <div style={{backgroundColor: "transparent",borderRadius: "3px"}}>
					              </div>
						            <br />
				              </div>
                      <br />
				              <input type="submit" value="Sign in" class="sign-in-buttonOFF loginBtn" style={{marginLeft: "auto", marginRight: "auto"}} />
			              </form>

			              <button onclick="window.location.href = {% url 'register' %}" class="create-account-buttonOFF loginBtn" type="button">
				              Create Account
			              </button>
		              </div>
	              </div>


	              <div class="col-sm-12 col-lg-8 my-auto">
		              <img src= 'assets/images/login.png' alt="login" style={{maxHeight: "700px", padding: "50px"}} />
	              </div>
             </div>
           </div>  
          <Footer />
        </div>
    );
};

export default Login;
