import React, {Component } from 'react';
import './Play.css';
import { useNavigate, useLocation } from 'react-router-dom';

const Play = () => {
  const room =location.state;
  console.log("roomhere", room);
  const navigate = useNavigate();
  const handleStartGame = async () => {
    try {
        const response = await fetch('http://localhost:8000/api/start-game/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                // Add any authentication headers if needed
            },
            // Add any request body if needed
        });

        if (response.ok) {
            // Handle success, e.g., show a success message
            console.log('Game started successfully');
            navigate('/rules')
        } else {
            // Handle error, e.g., show an error message
            console.error('Failed to start the game');
        }
    } catch (error) {
        console.error('Error during API call:', error);
    }
  };
      return (
        <div>
          <div className="header">
            <img className="header-img" src='assets/images/logo-white.png'></img>
          </div>
          <div className="">
            <div className="hero-content text-center">

                <h1 className="text-5xl font-bold">Game Lobby</h1>
                <p >
                  Please start the game when all players have joined.
                </p>
                <p>Connected players</p>
                <button
                  x-show="isOwner === 1"
                  className="btn btn-primary"
                  onClick={handleStartGame}
                  >
                  Start
                </button>
              </div>

        </div>
      </div>
      );
    }

export default Play;
