import React from 'react';
import './Play.css';
import { useNavigate, useLocation } from 'react-router-dom';

const Play = () => {
  const location = useLocation();
  const [currentUrl, setCurrentUrl] = useState('');
  const room =location.state;
  console.log("roomhere", room);

  const navigate = useNavigate();
  const handleStartGame = async () => {
    try {
        const response = await fetch('http://localhost:8000/api/start-game/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                // Add any authentication headers if needed
            },
            // Add any request body if needed
        });

        if (response.ok) {
            // Handle success, e.g., show a success message
            console.log('Game started successfully');
            navigate('/rules')
        } else {
            // Handle error, e.g., show an error message
            console.error('Failed to start the game');
        }
    } catch (error) {
        console.error('Error during API call:', error);
    }
  };
      return (
        <div>
          <div className="header">
            <img className="header-img" src="/assets/images/logo-white.png" alt = "Phooled header logo"></img>
          </div>
          <div className="hero-content-container text-center">
            <div className="hero-content">

                <h1 className="hero-text">Game Lobby</h1>
                <div className="hero-subcontainer">
                  <div className="card-body">
                    <h2 className='card-title'>Connected players</h2>
                    <div>
                      <p className="invite-text">{room.Room.room_players}</p>
                    </div>
                    <h2 className='card-title'>
                      Invite players
                    </h2>
                    <p className="invite-text">Share the invite link below to let other players join this game!</p>
                    <label>
                      <input type="text" value={currentUrl}></input>
                    </label>

                    <p>http://localhost:3000/{room.Room.room_url}</p>
                  </div>
                </div>
                <button
                      x-show="isOwner === 1"
                      className="btn btn-primary"
                      onClick={handleStartGame}
                    >
                      Start
                    </button>
              </div>

        </div>
      </div>
      );
    }

export default Play;
