// src/components/Signup.js

import React, { useState } from 'react';
import axios from 'axios';

const Signup = ({ history }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSignup = async () => {
        try {
            const response = await fetch('http://localhost:8000/api/signup/', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({ username, password }),
            });

            if (response.ok) {
              // Signup successful, you might want to redirect to login or do other actions
              console.log('Signup successful');
            } else {
              // Signup failed, check the response for specific error message
              const data = await response.json();
              setError(data.message); // Assuming the server sends a message field in the response
              if (data.message === 'User already exists') {
                // User already exists, navigate to the login page
                navigate('/login');
              }
            }
          } catch (error) {
            console.error('Error during signup:', error);
          }
    };

    return (
        <div>
            <h1>Signup</h1>
            <label>
                Username:
                <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <br />
            <label>
                Password:
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <br />
            <button onClick={handleSignup}>Signup</button>
        </div>
    );
};

export default Signup;
