import React from 'react';
import Header from '../Header/Header';
import { useNavigate ,useLocation} from 'react-router-dom';

import './Game.css';
const Game = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const createRoomAPI = async () => {
    try {
      const user =location.state;
      console.log("yha tak aya ", user);
      const response = await fetch('http://localhost:8000/api/create-room', {

        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // Add any other headers if needed
        },
        body: JSON.stringify({
          roomName: "roomxyz",  // Replace with your desired room name
          admin: user.User.user_id,
        }),
      });
      console.log("response",response)
      if (response.ok) {
        const data = await response.json();
        console.log("game data",data);
        // Room created successfully, redirect to the Play page
        navigate('/play');
      } else {
        // Handle the case where room creation failed
        console.error('Error creating room:', response.statusText);
      }
    } catch (error) {
      console.error('Error creating room:', error);
    }
  };

  return (
      <div>
       <Header />
       <div id="main" className="contentOFF" role="main">
          <div className="container">
	          <div className="row">
              <button className="startbutton" onClick={createRoomAPI}>Start new game</button>
            </div>
          </div>
        </div>
      </div>
  );

};

export default Game;
